CREATE TABLE "ENTRIES"(
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 686) NOT NULL,
    "ADDITIONAL_INFO" VARCHAR(4096),
    "ADDRESS" VARCHAR(1024),
    "ALIAS" VARCHAR(1024),
    "DOB" VARCHAR(255),
    "ENTRY_TYPE" VARCHAR(255),
    "FULL_NAME" VARCHAR(255),
    "IDENTITY_NO" VARCHAR(255),
    "JOB_TITLE" VARCHAR(2048),
    "LOAD_DATE" DATE,
    "NAME1" VARCHAR(255),
    "NAME2" VARCHAR(255),
    "NAME3" VARCHAR(255),
    "NAME4" VARCHAR(255),
    "NATIONALITY" VARCHAR(255),
    "PASSPORT_NO" VARCHAR(255),
    "POB" VARCHAR(255),
    "SOURCE_LIST" VARCHAR(255),
    "TITTLE" VARCHAR(255),
    CONSTRAINT "PK_ENTRIES" PRIMARY KEY("ID")
);

CREATE TABLE "USERS"(
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 6) NOT NULL,
    "EMAIL" VARCHAR(255),
    "NAME" VARCHAR(255),
    "PASSWORD" VARCHAR(255),
    "ROLE" VARCHAR(10), -- вместо ENUM используем VARCHAR
    "STATUS" BOOLEAN,
    "USERNAME" VARCHAR(255),
    CONSTRAINT "PK_USERS" PRIMARY KEY("ID"),
    CONSTRAINT "UK_USERS_USERNAME" UNIQUE("USERNAME")
);
